svg.script:

var svgns = "http://www.w3.org/2000/svg";
var xlink = "http://www.w3.org/1999/xlink";
var container;
var interval;
var rotate = 0;

var time = function(){
	var r = rotate+90;
	return Math.sin(r/180*Math.PI)*100+100;
};

var setRotate = function(){
	rotate += 15;
	container.setAttribute('transform', 'rotate('+rotate+')');
	interval = setTimeout(setRotate, time());
};

window.addEventListener('load', function(){
	container = document.getElementById('container');
	for(var i = 0; i < 24; i++){
		var line = document.createElementNS(svgns, 'use');
		line.setAttributeNS(xlink, 'xlink:href', '#l1');
		var deg = i*15;
		line.setAttribute('transform', 'rotate(' + deg + ')');
		line.setAttribute('opacity', deg/360-.1);
		container.appendChild(line);
	}
	interval = setTimeout(setRotate, time());
}, false);
